<style>* {box-sizing: border-box;}html {overflow-X: hidden;}*, *::before, *::after {box-sizing: inherit;}html, body {margin: 0;padding: 0;scroll-behavior: smooth;}.page-sect {max-width: 1200px;margin: 0 auto;}a, a:hover {text-decoration: none;cursor: pointer;}ul, li {padding: 0;margin: 0;}.header-contain {text-align: center;background-color: #005ca9;font-family: "Frijole", sans-serif;z-index: 9999;}.logo {display: block;width: 100px;}.fb-icon {width: 40px;}.nav-ul {display: flex;align-items: flex-end;margin: 0;padding: 0;}.menu {flex: 1 0 auto;padding: 10px;color: white;letter-spacing: .1em;list-style-type: none;text-align: center;}a {color: white;}.menu:first-child {flex: 3 0 auto;}.menu:nth-child(5) {flex: 3 0 auto;text-align: right;line-height: 1.5;}.menu-a {color: white;text-shadow: 0px 0px 3px white;}.menu-a:hover, .email-address:hover {border-bottom: 2px solid rgba(255, 255, 255, 0.8);}.contact-info:hover {border: none;}.trigram {position: absolute;top: 0;right: 0;width: 30px;margin: 17px 17px 0 0;}.hero-contain {position: relative;background-image: url("images/placeholder-image-001.jpg");background-position: 50% 50%;background-size: cover;}.hero-text-contain {position: absolute;top: 0;left: 0;background-color: rgba(255, 255, 255, 0.8);}.hero-twins {width: 100%;height: 60vh;}.hero-h1, .hero-h2, .hero-p {margin: 0 auto;line-height: 1;text-align: center;color: #005ca9;}.hero-h1 {margin-top: 30px;padding: 0 3vw 0 3vw;font-size: 90px;font-family: 'Frijole', sans-serif;font-weight: 400;text-shadow: 3px 3px 10px rgba(0, 0, 0, 0.5);}.hero-h2, .hero-p, .quote-btn, .menu, .sub-menu, .sub-menu-a, .slogan, .paragraph, .services-p, span, #toTop, .service-menu-h3, .service-menu-item, .close, textarea, .footer-contain, .service-h3, .get-in-touch, .thank-you-h1, .thank-you-h2, .thank-you-p {font-family: 'Open Sans', sans-serif;}.hero-h2 {margin-top: 20px;padding: 0 3vw 0 3vw;font-size: 30px;font-weight: 800;}.hero-p {max-width: 900px;padding: 0 20px;font-size: 20px;letter-spacing: .1em;}.quote-btn {display: block;padding: 10px;margin: 0 auto;margin-top: 30px;outline: none;border: none;border-radius: 7px;font-size: 18px;background-color: rgb(255, 123, 0);color: white;box-shadow: 3px 3px 8px rgba(0, 0, 0, 0.6);}</style>