<style>html{overflow-X:hidden}*,::after,::before{box-sizing:inherit}body,html{margin:0;padding:0;scroll-behavior:smooth}.page-sect{max-width:1200px;margin:0 auto}li,ul{padding:0;margin:0}.header-contain{text-align:center;background-color:#005ca9;font-family:Frijole,sans-serif;z-index:9999}.logo{display:block;width:100px}.nav-ul{display:flex;align-items:flex-end;margin:0;padding:0}.menu{flex:1 0 auto;padding:10px;color:#fff;letter-spacing:.1em;list-style-type:none;text-align:center}a{color:#fff}.menu:first-child{flex:1 0 auto}.menu:nth-child(5){flex:3 0 auto;text-align:right;line-height:1.5}.menu-a{color:#fff;text-shadow:0 0 3px #fff}.email-address:hover,.menu-a:hover{border-bottom:2px solid rgba(255,255,255,.8)}.contact-info:hover{border:none}.trigram{position:absolute;top:0;right:0;width:30px;margin:17px 17px 0 0}.fb-icon{width:40px;position:absolute;top:15px;right:10px}.hide-desktop{display:none}.hero-contain{position:relative;background-image:url(images/placeholder-image-001.jpg);background-position:50% 50%;background-size:cover}.hero-text-contain{position:absolute;top:0;left:0;background-color:rgba(255,255,255,.8)}.hero-twins{width:100%;height:60vh}.hero-h1,.hero-h2,.hero-p{margin:0 auto;line-height:1;text-align:center;color:#005ca9}.hero-h1{margin-top:30px;padding:0 3vw 0 3vw;font-size:90px;font-family:Frijole,sans-serif;font-weight:400;text-shadow:3px 3px 10px rgba(0,0,0,.5)}#toTop,.close,.footer-contain,.get-in-touch,.hero-h2,.hero-p,.menu,.paragraph,.quote-btn,.service-h3,.service-menu-h3,.service-menu-item,.services-h2,.services-p,.slogan,.sub-menu,.sub-menu-a,.thank-you-h1,.thank-you-h2,.thank-you-p,span,textarea{font-family:'Open Sans',arial}.hero-h2{margin-top:20px;padding:0 3vw 0 3vw;font-size:30px;font-weight:800}.hero-p{max-width:900px;padding:0 20px;font-size:20px;letter-spacing:.1em}.quote-btn{display:block;padding:10px;margin:0 auto;margin-top:30px;outline:0;border:none;border-radius:7px;font-size:18px;background-color:#ff7b00;color:#fff;box-shadow:3px 3px 8px rgba(0,0,0,.6)}.slogan{padding:10px;margin:0;text-align:center;background-color:#005ca9;color:#fff;font-size:26px;letter-spacing:2px;text-shadow:0 0 3px #fff}.question{padding-top:25px;font-size:24px;text-align:center}@media screen and (orientation:landscape) and (max-width:1000px){.hero-twins{height:75vh}}@media screen and (max-width:1001px){.hero-h1{font-size:8vw;margin-top:3vh}.hero-h2,.hero-p,.quote-btn{margin-top:3vh}.hero-h2{font-size:6vw}.hero-p{font-size:16px}}@media screen and (max-width:801px){.hide-mobile{display:none}.hide-desktop{display:block}.logo{height:40px}.fb-icon{top:10px;right:75px}}@media screen and (max-width:601px){.slogan{font-size:3vw}}@media screen and (orientation:portrait) and (max-width:501px){.hero-twins{height:50vh}}</style>